mapper.select:
select
vbs.*
from view_board_status vbs
left join view_board_info vbi on vbs.view_board_info_id = vbi.id
where vbi.view_board_type = #{freedViewBoardType,jdbcType=VARCHAR}
    and vbs.view_board_status_name_id = #{status,jdbcType=VARCHAR}

mapper.update:
update view_board_status
set view_board_info_id = #{viewBoardInfoId,jdbcType=BIGINT},
  view_board_status_name_id = #{viewBoardStatusNameId,jdbcType=BIGINT},
  current_status_quantities = #{currentStatusQuantities,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}
